export const skeleton=' \nlet up = 8;\nlet left = 9;\nlet down = 10;\nlet right = 11;\nlet size = 64;\n\nlet map = new Map();\nmap.set("up", up * size);\nmap.set("left", left * size);\nmap.set("down", down * size);\nmap.set("right", right * size);\nmap.set(8, up * size);\nmap.set(9, left * size);\nmap.set(10, down * size);\nmap.set(11, right * size);\n\nclass Skeleton {\n  constructor({\n    img,\n    frameX = 0,\n    frameY = 10,\n    width = 64,\n    height = 64,\n    x = 0,\n    y = 0,\n  }) {\n    this.img = img;\n    this.frameX = frameX;\n    this.frameY = frameY * height;\n    this.width = width;\n    this.height = height;\n    this.x = x;\n    this.y = y;\n    this.img = new Image();\n    this.img.src = "./components/skeleton/skeleton.png";\n    this.scaleWidth = this.width * 1.5;\n    this.scaleHeight = this.height * 1.5;\n    this.speed = 5;\n    this.isIdle = true;\n  }\n  update(start = 0, end = 8, offset = 1) {\n    if (this.frameX >= this.width * end * offset) {\n      this.frameX = start;\n    }\n    this.frameX += this.width * offset;\n  }\n\n  up() {\n    this.frameY = map.get("up");\n    this.y -= this.speed;\n  }\n\n  left() {\n    console.log(\'left\');\n    this.frameY = map.get("left");\n    this.x -= this.speed;\n  }\n\n  down() {\n    this.frameY = map.get("down");\n    this.y += this.speed;\n  }\n\n  right() {\n    this.frameY = map.get("right");\n    this.x += this.speed;\n  }\n\n  idle() {\n    this.frameY = map.get(this.frameY / this.height);\n    this.frameX = 0;\n    this.isIdle = true;\n  }\n}\nconst canvas = document.createElement("canvas");\ncanvas.classList.add("canvas");\ncanvas.classList.add(\'componentInner\');\n\nconst u = document.createElement(\'button\');\nconst l = document.createElement(\'button\');\nconst r = document.createElement(\'button\');\nconst d = document.createElement(\'button\');\nconst btns = [l,r,u,d];\nconst dir = [\'u\',\'l\',\'r\',\'d\'];\nconst arr = [\'^\',\'<\',\'>\',\'v\'];\nlet TOUCH = false;\n\nconst gamepad = document.createElement(\'div\');\ngamepad.classList.add(\'gamepad\');\n\nfor(let i = 0; i < btns.length; i++){\n   btns[i].classList.add(\'gamepad-btn\');\n   btns[i].innerText = arr[i];\n   btns[i].value = arr[i];\n   btns[i].setAttribute(\'id\', dir[i]);\n   btns[i].addEventListener(\'pointerdown\',(e)=>{\n      const interval = setInterval(()=>{\n      handleTouch(e); \n    }, 100)\n    document.body.addEventListener(\'pointerup\',(e)=>{\n     clearInterval(interval);\n     keyUp();\n    });\n  });\n\n   gamepad.appendChild(btns[i]);\n};\n\ndocument.getElementById(\'skeleton\').prepend(gamepad);\ndocument.getElementById(\'skeleton\').prepend(canvas);\nconst context = canvas.getContext("2d");\nconst centerX = canvas.width / 2;\nconst centerY = canvas.height / 2;\n\nlet player; \n\nfunction clear() {\n  context.clearRect(0, 0, canvas.width, canvas.height);\n  context.fillStyle = "white";\n  context.fillRect(0,0,canvas.width, canvas.height);\n}\n\nfunction draw(obj) {\n  let offset = 1;\n  let updateParams = [0];\n  let scaleX = 0;\n  let scaleY = 0;\n  if (obj.isIdle) {\n    updateParams.push(0);\n  } else {\n    updateParams.push(8);\n  }\n  context.drawImage(\n    obj.img,\n    obj.frameX,\n    obj.frameY,\n    obj.width * offset,\n    obj.height * offset,\n    obj.x - obj.width - scaleX,\n    obj.y - obj.width - scaleY,\n    obj.scaleWidth * offset,\n    obj.scaleHeight * offset\n  );\n  obj.update(...updateParams, offset);\n}\n\nfunction paint() {\n  clear();\n  draw(player);\n}\nfunction keydown(e, player) {\n  player.isIdle = false;\n  switch (e.key) {\n    case "ArrowRight":\n      player.right();\n      break;\n    case ">":\n      player.right();\n      break;\n    case "ArrowLeft":\n      player.left();\n      break;\n    case "<":\n      player.left();\n      break;\n    case "ArrowUp":\n      player.up();\n      break;\n    case "^":\n      player.up();\n      break;\n    case "ArrowDown":\n      player.down();\n      break;\n    case "v":\n      player.down();\n      break;\n  }\n}\nfunction handleTouch(e) {\n  e.key = e.target.value;\n  keydown(e, player);\n}\n\nfunction keyUp() {\n  player.idle();\n}\n\ndocument.addEventListener("keydown", (e) => keydown(e, player));\ndocument.addEventListener("keyup", keyUp);\nwindow.onload = ()=>{\n  player = new Skeleton({ x: centerX, y: centerY, context });\n  setInterval(paint, 1000 / 15);\n}\n';